package com.company;import java.util.Stack;/** * Created by Hoshiko on 10/16/15. */public class CharacterOrder {    public static boolean character (String string){        Stack<Character> stack = new Stack<Character>();        int result = 0;        for (int i = 0 ; i < string.length(); i++) {            char chara = string.charAt(i);            if(chara == '{' || chara == '(' || chara == '[') {                stack.push(chara);            }else {                if(!stack.empty()) {                    char lastChara = stack.pop();                    if(lastChara == '(' && chara == ')') {                    }else if(lastChara == '{' && chara == '}') {                    }else if(lastChara == '[' && chara == ']') {                    }else {                        result += 1;                        break;                    }                }else {                    result += 1;                    break;                }            }        }        if (result == 0 ){            return true;        }else {            return false;        }    }}